import React from 'react';
import Form from '../form';
import image from '/home/lms/react/inmotion/src/wordcloud/OIP.jpeg';
import './wordcloud.css'
import { useState } from 'react'
import axios from 'axios';
import { useRef } from 'react';


const Wordcloud=()=>{

    const enviarWord = (form) =>{
        return axios.get('http://localhost:8000/wc/wordcloud/',
        {
            params : form,
        })
        .then(response => {
          console.log(response.data);
        })
    }
    const receberNuvem = () =>{
        return axios.get('http://localhost:8000/wc/cloud/')
        .then(response => {
          console.log(response.data['image']);
          setImageurl(response.data['image'])
        })
    }

    const [enviado, setEnviado] = useState(false)
    const [value, setValue] = useState('')
    const [imageurl, setImageurl] = useState('')
    const bottomEl = useRef(null);

    const aoSubmter = (evento) => {
        evento.preventDefault()
        console.log('Form foi submetido')
        setEnviado(true)
        enviarWord({valor : value})
    }

    const aoResetar = () => {
        console.log('Form foi resetado')
        setEnviado(false)
        receberNuvem()
        scrollToBottom()

    }

    const scrollToBottom = () => {
        bottomEl?.current?.scrollIntoView({ behavior: 'smooth' });
      };


    return (

            <div className='wordcloud'>
            <div className='body'>
                <img src={image} alt="logo"/>
            <div className='formulario'>
                <Form aoenviar={aoSubmter} aoresetar={aoResetar} aoalterar={value => setValue(value)} />

            </div>   
            </div>
            <div ref={bottomEl}>
                {imageurl ? 
            <img src= {`data:image/png;base64,${imageurl}`} alt = "wordcloud"/>
            : 
            <div>
            </div>  
            }
            </div>
            </div>
        )
}
 export default Wordcloud
