import Button from '../button';

const Wordcloud=()=>{

    const [enviado, setEnviado] = useState(0)


    return (
            <div className='wordcloud'>
            <div className='body'>
                <img src={image} alt="logo"/>
            <div className='form'>
                <Form aoenviar={enviado => setEnviado(1)} />
                {enviado ? <Button texto="Nuvem"/>:
                <div></div>
                }

            </div>
            </div>
            </div>
        )
}
 export default Wordcloud
